import React from 'react';
import { Canvas, useFrame, useRef } from 'react-three-fiber';
import Particle from './Particle';
import { createGlobalStyle } from 'styled-components';
import styled from 'styled-components';

const GlobalStyle = createGlobalStyle`
  html, body {
    min-height: 100vh;
    min-width: 100vw;
    margin: 0;
    padding: 0;
  }

  canvas {
      height: 100vh;
      width: 100vw;
      position: fixed:
      top: 0;
      left: 0;
  }
`;

const HUD = styled.span`
  position: fixed;
  top: 0;
  left: 0;
  margin: 1rem;
  font-family: sans-serif;
  font-size: 3rem;
`;

class Scene extends React.Component {
  constructor() {
    super();
    this.state = {
      particles: 0,
    };
    this.group = useRef();
  }

  componentWillReceiveProps(props) {
    this.setState({ particles: props.users });
  }

  randCoord() {
    let x = Math.random() * 2 - 1;
    let y = Math.random() * 2 - 1;
    let z = Math.random() * 2 - 1;
    return [x, y, z];
  }

  randColor() {
    let r = Math.floor(Math.random() * 120);
    let g = Math.floor(Math.random() * 255);
    let b = Math.floor(Math.random() * 0);
    return [r, g, b];
  }

  componentDidMount() {
    useFrame(
      () => (this.group.current.rotation.x = mesh.current.rotation.y += 0.01)
    );
  }

  render() {
    return (
      <>
        <GlobalStyle />
        <HUD>{this.state.particles} Active Users</HUD>

        <Canvas
          pixelRatio={window.devicePixelRatio ? window.devicePixelRatio : 1}
        >
          <ambientLight />
          <pointLight position={[10, 10, 10]} />

          <group ref={group}>
            {[...Array(this.state.particles)].map((e, i) => (
              <Particle
                key={i}
                position={this.randCoord()}
                color={this.randColor()}
              />
            ))}
          </group>
        </Canvas>
      </>
    );
  }
}

export default Scene;
